$_MODULE['<{younitedpay}prestashop>states-form_0bf421443af9ff277cce958bbfd2c92f'] = 'Por ley, el préstamo sólo puede ser activado tras entregarse el bien al cliente. Por favor, asegúrate de que el estatus elegido se ajusta a esta norma';
